-- Select a las publicaciones de 2 subcategorias
SELECT * FROM `Publicaciones`.`Publicaciones`
WHERE `Categorias_idCategorias` IN (SELECT idCategorias FROM `Publicaciones`.`Categorias` WHERE tipo = 2)
LIMIT 1;

-- Select a todas las publicaciones del usuario con id 8, 10, 12
SELECT * FROM `Publicaciones`.`Publicaciones`
WHERE `Publicaciones`.`Usuarios_idUsuarios` IN (8, 10, 12);

-- Select con inner join entre publicaciones y usuarios
SELECT * FROM `Publicaciones`.`Publicaciones`
INNER JOIN `Publicaciones`.`Usuarios` ON `Publicaciones`.`Usuarios_idUsuarios` = `Usuarios`.`idUsuarios`;

-- Select con left join entre publicaciones y usuarios
SELECT * FROM `Publicaciones`.`Publicaciones`
LEFT JOIN `Publicaciones`.`Usuarios` ON `Publicaciones`.`Usuarios_idUsuarios` = `Usuarios`.`idUsuarios`;

-- Select con right join entre publicaciones y usuarios
SELECT * FROM `Publicaciones`.`Publicaciones`
RIGHT JOIN `Publicaciones`.`Usuarios` ON `Publicaciones`.`Usuarios_idUsuarios` = `Usuarios`.`idUsuarios`;

-- Select con inner join entre publicaciones y comentarios
SELECT * FROM `Publicaciones`.`Publicaciones`
INNER JOIN `Publicaciones`.`Comentarios` ON `Publicaciones`.`idPublicaciones` = `Comentarios`.`Publicaciones_idPublicaciones`;

-- Select con left join entre publicaciones y comentarios
SELECT * FROM `Publicaciones`.`Publicaciones`
LEFT JOIN `Publicaciones`.`Comentarios` ON `Publicaciones`.`idPublicaciones` = `Comentarios`.`Publicaciones_idPublicaciones`;

-- Select con right join entre publicaciones y comentarios
SELECT * FROM `Publicaciones`.`Publicaciones`
RIGHT JOIN `Publicaciones`.`Comentarios` ON `Publicaciones`.`idPublicaciones` = `Comentarios`.`Publicaciones_idPublicaciones`;

-- Script de update por lo menos a 5 publicaciones
UPDATE `Publicaciones`.`Publicaciones`
SET titulo_publicacion = 'Nuevo título', descripcion = 'Nuevo contenido'
WHERE idPublicaciones IN (1, 3, 5, 6, 8);

